class Car
{
	String brand;
	String model;
	int year;
	
	public Car(String  br, String mod, int yr)
	{
		brand = br;
		model = mod;
		year = yr;
	}
	
	public String getBrand()
	{
		return brand;
		
	}
	
	public String getModel()
	{
		return model;
		
	}
	
	public int getYear()
	{
		return year;
	}
	
	
}